		public static async Task CreatePhotos(SQLiteAsyncConnection conn)
		{
			string query = "INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(1, 'Varsity Soccer', 'Pep Rally 2022!! Go Eagles!', 'img/photos/soccer.jpeg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(2, 'JSA Conference', 'JSA Chapter attended the 2022 Fall State Convention held in Philadelphia, Pennsylvania. In addition to actively debating and participating in climate change activism, students were able to bring home 6 gavels for best speaker awards.', 'img/photos/jsa.jpeg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(3, 'Quiz Bowl', 'Academic Team (Quiz Bowl) traveled to Manasquan High School to compete at the Jersey Shore Introductory Tournament. Both of our teams had amazing performances!', 'img/photos/quizbowl.jpeg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(4, 'NJIT Tour', 'Some quick example text to build on the card title and make up the bulk of the card''s content.', 'img/photos/njit.jpg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(5, 'Model UN', 'Students participated in the second annual MCA Model United Nations Conference held in Middlesex College''s Crabiel Hall. During the conference, students participated in 3 exciting committees on topics ranging from the social and civil unrest in the Central African Republic to the Titan takeover of Olympus.', 'img/photos/un.jpg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(6, 'Science Olympiad', 'Science Olympiad team competed in the Union College Regional Tournament. Fifteen team members participated in 12 different events against teams from across Central Jersey. The team ranked 7th overall, qualifying for the state competition.', 'img/photos/scioly.jpg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(7, 'Winter Ball', 'Edison Academy Magnet School''s Senior Class held their Winter Formal! Everyone dressed to impress and had a blast dancing the night away!', 'img/photos/dance.jpg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(8, 'RWJ Safety Ambassadors', 'Our Safety Ambassadors taught the students helmet safety, safety in & around cars, roads/traffic and safety on stairs.', 'img/photos/ambassadors.jpg');INSERT INTO photos (ID, TITLE, DESCRIPTION, \"PATH\") VALUES(9, 'Senior Capstones', 'Last Thursday our seniors presented their technology capstone projects to a crowd of faculty, family and students. After devoting a year to developing their projects, each team successfully demonstrated their inventions. Congratulations, seniors, on your impressive achievements!', 'img/photos/capstone.jpg');";
            string[] queries = query.Split(new[] { ';' }, StringSplitOptions.RemoveEmptyEntries);
            foreach (string q in queries)
            {
                await conn.ExecuteAsync(q);
            }
        }
